mapper.select:

    SELECT A.CUST_SEQ,
			   A.CUST_NM,
			   A.BIZNO,
			   A.CUST_TYPE_CD,
			   B.DTL_CD_NM AS CUST_TYPE,
			   A.TLNO,
			   A.FXNO,
			   A.CNT_PSN_NM,
			   A.CNT_PSN_POS,
			   A.CNT_PSN_HPNO,
			   A.CNT_PSN_EML,
			   A.CRETR,
			   A.CRET_DT,
			   A.UPDTR,
			   A.UPDT_DT,
			   COUNT(*) OVER() AS TOTAL_CNT
	FROM REMS.CUST_INF A
	LEFT OUTER JOIN REMS.CD_DTL B ON A.CUST_TYPE_CD = B.DTL_CD AND B.GRP_CD = 'CUST_TYPE_CD'
	WHERE A.DEL_YN <> 'Y'


		 	AND A.CUST_NM like '%' || #{custNm} || '%' 



		 	AND A.BIZNO like '%' || #{bizNo} || '%' 



		 	AND A.CNT_PSN_NM like '%' || #{cntPsnNm} || '%' 



		 	AND A.CUST_TYPE_CD = #{custTypeCd} 




				AND 

				( 
					A.CUST_SEQ IN
					(
						SELECT CUST_SEQ FROM REMS.SITE_INF WHERE SITE_SEQ IN  

							#{item}

					) 


						OR  ( SELECT COUNT(*) FROM REMS.SITE_INF WHERE CUST_SEQ = A.CUST_SEQ ) = 0

				)


				AND A.CUST_SEQ IN (-99)



	 ORDER BY A.CUST_SEQ ${sort} 
	 ORDER BY A.CUST_NM ${sort} 
	 ORDER BY A.BIZNO ${sort} 
	 ORDER BY A.CUST_TYPE_CD ${sort} 
	 ORDER BY A.TLNO ${sort} 
	 ORDER BY A.CNT_PSN_NM ${sort} 
	 ORDER BY A.CNT_PSN_HPNO ${sort} 		
	 ORDER BY A.CRET_DT ${sort} 

	LIMIT #{length} offset #{start}

SELECT A.CUST_SEQ, A.CUST_NM, A.BIZNO, A.CUST_TYPE_CD, B.DTL_CD_NM AS CUST_TYPE, A.TLNO, A.FXNO, A.CNT_PSN_NM, A.CNT_PSN_POS, A.CNT_PSN_HPNO, A.CNT_PSN_EML, COALESCE(DEV_SUM.TOTAL_CPCT, 0) AS TOTAL_CPCT, COALESCE(NOW_MON_SUM.MONTH_GENT_QNT, 0) AS MONTH_GENT_QNT, COALESCE(AF_MON_SUM.MONTH_GENT_QNT, 0) AS AF_MONTH_GENT_QNT, COALESCE(NOW_MON_SUM.MONTH_GENT_TIME, 0) AS MONTH_GENT_TIME, COALESCE(NOW_MON_SUM.TOTAL_GENT_PROFT_AMT, 0) AS TOTAL_GENT_PROFT_AMT, trunc(COALESCE((NOW_MON_SUM.MONTH_GENT_QNT - AF_MON_SUM.MONTH_GENT_QNT) / AF_MON_SUM.MONTH_GENT_QNT * 100, 0), 2) AS MONTH_CHANGE_RATE, A.CRETR, A.CRET_DT, A.UPDTR, A.UPDT_DT, COUNT(*) OVER() AS TOTAL_CNT FROM REMS.CUST_INF A LEFT OUTER JOIN REMS.CD_DTL B ON A.CUST_TYPE_CD = B.DTL_CD AND B.GRP_CD = 'CUST_TYPE_CD'
LEFT OUTER JOIN ( SELECT SI.CUST_SEQ, SUM(COALESCE(GD.TDAY_GENT_QNT, 0)) AS MONTH_GENT_QNT, SUM(COALESCE(GD.TDAY_GENT_TIME, 0)) AS MONTH_GENT_TIME, SUM(COALESCE(GD.RDUC_AMT, 0)) AS TOTAL_GENT_PROFT_AMT FROM REMS.GENT_DAY_SUM GD INNER JOIN REMS.SITE_INF SI ON GD.SITE_SEQ = SI.SITE_SEQ WHERE to_char(to_date(GD.TGT_DATE, 'YYYYMMDD'), 'YYYYMM') = to_char(now(), 'YYYYMM') GROUP BY SI.CUST_SEQ ) AS NOW_MON_SUM ON A.CUST_SEQ = NOW_MON_SUM.CUST_SEQ
LEFT OUTER JOIN ( SELECT SI.CUST_SEQ, SUM(COALESCE(GD.TDAY_GENT_QNT, 0)) AS MONTH_GENT_QNT, SUM(COALESCE(GD.TDAY_GENT_TIME, 0)) AS MONTH_GENT_TIME, SUM(COALESCE(GD.RDUC_AMT, 0)) AS TOTAL_GENT_PROFT_AMT FROM REMS.GENT_DAY_SUM GD INNER JOIN REMS.SITE_INF SI ON GD.SITE_SEQ = SI.SITE_SEQ WHERE to_char(to_date(GD.TGT_DATE, 'YYYYMMDD'), 'YYYYMM') = to_char(now(), 'YYYYMM') GROUP BY SI.CUST_SEQ ) AS AF_MON_SUM ON A.CUST_SEQ = AF_MON_SUM.CUST_SEQ
LEFT OUTER JOIN ( SELECT SI.CUST_SEQ, SUM(COALESCE(DI.INSTL_CPCT, 0)) AS TOTAL_CPCT FROM REMS.DEV_INF DI INNER JOIN REMS.SITE_INF SI ON DI.SITE_SEQ = SI.SITE_SEQ WHERE DI.SVC_CD = 'PV_POW' GROUP BY SI.CUST_SEQ ) AS DEV_SUM ON A.CUST_SEQ = DEV_SUM.CUST_SEQ
AND A.CUST_SEQ = ${custSeq}
AND A.CUST_NM = #{custNm}
AND A.BIZNO = #{bizNo}

mapper.insert:
 INSERT INTO REMS.CUST_INF (cust_seq, 
 								   cust_nm, 
 								   bizno, 
 								   cust_type_cd, 
 								   tlno, 
 								   fxno, 
 								   cnt_psn_nm, 
 								   cnt_psn_pos, 
 								   cnt_psn_hpno, 
 								   cnt_psn_eml, 
 								   del_yn, 
 								   cretr, 
 								   cret_dt)
						 VALUES(nextval('rems.site_inf_seq')
						 			,#{custNm}
						 			,#{bizNo}
						 			,#{custTypeCd}
						 			,#{telNo}
						 			,#{faxNo}								 			
						 			,#{cntPsnNm}
						 			,#{cntPsnPos}
						 			,#{cntPsnHpno}
						 			,#{cntPsnEml}								 			
						 			,'N'							 			
						 			,#{cretr}
						 			,current_timestamp)
