mapper.select:

    SELECT A.EVT_SEQ,
		       A.OCCU_TOD,
		       B.SITE_NM,
			   C.DEV_NM,
			   A.EVT_CD,
			   A.EVT_NM,
			   A.EVT_DESC,
			   A.EVT_STS_CD,
			   A.CLR_YN,
			   A.CLR_TOD,
			   COUNT(*) OVER() AS TOTAL_CNT
	FROM REMS.EVT_HST A
	LEFT OUTER JOIN REMS.SITE_INF B ON A.SITE_SEQ = B.SITE_SEQ
	LEFT OUTER JOIN REMS.DEV_INF C ON C.DEV_SEQ = C.DEV_SEQ

	WHERE A.OCCU_TOD >= #{startDate} || '000000' AND A.OCCU_TOD <= #{endDate} || '000000'



				AND A.SITE_SEQ IN 

					#{item}



				AND A.SITE_SEQ IN (-99)



	 ORDER BY A.OCCU_TOD ${sort} 
	 ORDER BY A.OCCU_TOD ${sort} 
	 ORDER BY B.SITE_NM ${sort} 
	 ORDER BY C.DEV_NM ${sort} 
	 ORDER BY A.EVT_CD ${sort} 
	 ORDER BY A.EVT_NM ${sort} 
	 ORDER BY A.EVT_DESC ${sort} 
	 ORDER BY A.EVT_STS_CD ${sort} 
	 ORDER BY A.CLR_TOD ${sort} 


	LIMIT #{length} offset #{start}
