mapper.select:
SELECT A.SITE_SEQ, A.CUST_SEQ, A.SITE_NM, A.LAT, A.LNG, A.ZIPCD, A.ADDR, A.ADDR_ADT, A.DST_NM, A.DST_CD, A.RMRK, A.ADDR_SIDO, A.ADDR_SIGUNGU, A.ADDR_EUBMYUN, A.ADDR_DONGRI, A.CNT_PSN_HPNO, A.CNT_PSN_NM, SUM(F.GENT_QNT) AS GENT_QNT, SUM(F.ACCUM_GENT_QNT) AS ACCUM_GENT_QNT, SUM(G.INSTL_CPCT) AS INSTL_CPCT FROM REMS.SITE_INF A LEFT OUTER JOIN REMS.USR_SITE_REL B ON A.SITE_SEQ = B.SITE_SEQ LEFT OUTER JOIN REMS.GRP_SITE_REL C ON A.SITE_SEQ = C.SITE_SEQ LEFT OUTER JOIN REMS.USR_INF D ON B.USR_ID = D.USR_ID LEFT OUTER JOIN REMS.USR_INF E ON C.GRP_SEQ = E.GRP_SEQ LEFT OUTER JOIN REMS.INVRTR_OPRT_MIN_HST F ON A.SITE_SEQ = F.SITE_SEQ AND F.SVC_CD = 'PV_POW' LEFT OUTER JOIN REMS.DEV_INF G ON A.SITE_SEQ = G.SITE_SEQ AND G.SVC_CD = 'PV_POW'
WHERE D.USR_ID = #{usrId} or E.USR_ID = #{usrId}
GROUP BY A.SITE_SEQ, A.CUST_SEQ, A.SITE_NM, A.LAT, A.LNG, A.ZIPCD, A.ADDR, A.ADDR_ADT, A.DST_NM, A.DST_CD, A.RMRK, A.ADDR_SIDO, A.ADDR_SIGUNGU, A.ADDR_EUBMYUN, A.ADDR_DONGRI, A.CNT_PSN_HPNO, A.CNT_PSN_NM

SELECT A.SITE_SEQ, A.CUST_SEQ, CI.CUST_NM, A.SITE_NM, A.LAT, A.LNG, A.CONN_STS_CD, B.DTL_CD_NM AS CONN_STS, A.BIZ_TYPE_CD, C.DTL_CD_NM AS BIZ_TYPE, A.CONN_TYPE_CD, D.DTL_CD_NM AS CONN_TYPE, A.CNT_PSN_NM, A.CNT_PSN_HPNO, A.TLNO, A.CRET_DT, F.GENT_QNT, F.TDAY_GENT_QNT, F.ACCUM_GENT_QNT, DEV_SUM.TOTAL_CPCT AS INSTL_CPCT, COUNT(*) OVER() AS TOTAL_CNT FROM REMS.SITE_INF A INNER JOIN REMS.CUST_INF CI ON A.CUST_SEQ = CI.CUST_SEQ LEFT OUTER JOIN REMS.CD_DTL B ON A.CONN_STS_CD = B.DTL_CD AND B.GRP_CD = 'CONN_STS_CD' LEFT OUTER JOIN REMS.CD_DTL C ON A.BIZ_TYPE_CD = C.DTL_CD AND C.GRP_CD = 'BIZ_TYPE_CD' LEFT OUTER JOIN REMS.CD_DTL D ON A.CONN_TYPE_CD = D.DTL_CD AND D.GRP_CD = 'CONN_TYPE_CD'
LEFT OUTER JOIN ( SELECT DI.SITE_SEQ, SUM(COALESCE(DI.INSTL_CPCT, 0)) AS TOTAL_CPCT FROM REMS.DEV_INF DI WHERE DI.SVC_CD = 'PV_POW' GROUP BY DI.SITE_SEQ ) AS DEV_SUM ON A.SITE_SEQ = DEV_SUM.SITE_SEQ
LEFT OUTER JOIN ( SELECT SA.SITE_SEQ, SUM(SA.GENT_QNT) AS GENT_QNT, SUM(SA.TDAY_GENT_QNT) AS TDAY_GENT_QNT, SUM(SA.ACCUM_GENT_QNT) AS ACCUM_GENT_QNT FROM REMS.invrtr_oprt_min_hst SA LEFT JOIN REMS.invrtr_oprt_min_hst SB ON SA.SITE_SEQ = SB.SITE_SEQ AND SA.DEV_SEQ = SB.DEV_SEQ AND SA.SVC_CD = SB.SVC_CD AND SA.MSR_TOD < SB.MSR_TOD WHERE SB.SITE_SEQ IS NULL AND SA.SVC_CD = 'PV_POW' GROUP BY SA.SITE_SEQ ) F ON A.SITE_SEQ = F.SITE_SEQ
WHERE A.DEL_YN <> 'Y'
AND A.CUST_SEQ = ${custSeq}
AND A.SITE_NM like '%' || #{siteNm} || '%'
AND A.CONN_STS_CD = #{connStsCd}
AND A.BIZ_TYPE_CD = #{bizTypeCd}
AND A.CONN_TYPE_CD = #{connTypeCd}
AND A.CUST_SEQ IN ( SELECT CUST_SEQ FROM REMS.SITE_INF WHERE SITE_SEQ IN
#{item}
)
AND A.CUST_SEQ IN (-99)
ORDER BY A.SITE_SEQ ${sort} ORDER BY CI.CUST_NM ${sort} ORDER BY A.SITE_NM ${sort} ORDER BY A.CONN_STS_CD ${sort} ORDER BY A.CONN_TYPE_CD ${sort} ORDER BY A.BIZ_TYPE_CD ${sort} ORDER BY A.TLNO ${sort} ORDER BY A.CNT_PSN_NM ${sort} ORDER BY A.CNT_PSN_HPNO ${sort} ORDER BY A.CRET_DT ${sort}
LIMIT #{length} offset #{start}

SELECT A.SITE_SEQ, A.CUST_SEQ, A.SITE_NM, A.LAT, A.LNG, A.ZIPCD, A.ADDR, A.ADDR_ADT, A.DST_NM, A.DST_CD, A.RMRK, A.ADDR_SIDO, A.ADDR_SIGUNGU, A.ADDR_EUBMYUN, A.ADDR_DONGRI, A.CNT_PSN_HPNO, A.CNT_PSN_NM, F.GENT_QNT, F.TDAY_GENT_QNT, F.ACCUM_GENT_QNT, SUM(G.INSTL_CPCT) AS INSTL_CPCT FROM REMS.SITE_INF A LEFT OUTER JOIN REMS.DEV_INF G ON A.SITE_SEQ = G.SITE_SEQ AND G.SVC_CD = 'PV_POW' LEFT OUTER JOIN ( SELECT SA.SITE_SEQ, SUM(SA.GENT_QNT) AS GENT_QNT, SUM(SA.TDAY_GENT_QNT) AS TDAY_GENT_QNT, SUM(SA.ACCUM_GENT_QNT) AS ACCUM_GENT_QNT FROM REMS.invrtr_oprt_min_hst SA LEFT JOIN REMS.invrtr_oprt_min_hst SB ON SA.SITE_SEQ = SB.SITE_SEQ AND SA.DEV_SEQ = SB.DEV_SEQ AND SA.SVC_CD = SB.SVC_CD AND SA.MSR_TOD < SB.MSR_TOD WHERE SB.SITE_SEQ IS NULL AND SA.SVC_CD = 'PV_POW' GROUP BY SA.SITE_SEQ ) F ON A.SITE_SEQ = F.SITE_SEQ
WHERE A.SITE_SEQ = ${siteSeq}
GROUP BY A.SITE_SEQ, A.CUST_SEQ, A.SITE_NM, A.LAT, A.LNG, A.ZIPCD, A.ADDR, A.ADDR_ADT, A.DST_NM, A.DST_CD, A.RMRK, A.ADDR_SIDO, A.ADDR_SIGUNGU, A.ADDR_EUBMYUN, A.ADDR_DONGRI, A.CNT_PSN_HPNO, A.CNT_PSN_NM, F.GENT_QNT, F.ACCUM_GENT_QNT
